mapper.select:
SELECT count(*) FROM kjuns_content where (data_flag = 1  or data_flag =2)

	and section_id = #{sectionId}


	and title like CONCAT('%',#{title},'%')

SELECT id, title, subhead, summary, source, thumbnail, mind_map mindMap, content, data_flag dataFlag, 
create_by createBy,create_date createDate ,section_id sectionId,type_id type,isTop,isHot,
isDepth,isTease,user_id issuers FROM kjuns_content where (data_flag = 1  or data_flag =2)

	and section_id = #{sectionId}


	and title like CONCAT('%',#{title},'%')

order by create_date desc limit #{pageNo},#{pageSize}

SELECT id, title, subhead, summary, source, mind_map mindMap, thumbnail, content,data_flag dataFlag, 
create_by createBy,create_date createDate,type_id type,isTop,user_id issuers,
isHot,isDepth,isTease FROM kjuns_content where id=#{0}

mapper.update:
update kjuns_content set title = #{title}, subhead=#{subhead}, summary=#{summary},mind_map=#{mindMap},thumbnail=#{thumbnail} ,
source=#{source},content=#{content},update_by = #{updateBy}, update_date = #{updateDate}, type_id=#{type}, isTop=#{isTop},
isHot=#{isHot},isDepth=#{isDepth},isTease=#{isTease},user_id=#{issuers} where id = #{id}
