mapper.select:
SELECT id,mobile_phone mobilePhone,face_src faceSrc,sex,nick_name nickName,create_by createBy,data_flag dataFlag, create_date createDate,TIMESTAMPDIFF(DAY, create_date,CURRENT_TIMESTAMP()) automaticThoughts,location FROM kjuns_user_info where data_flag = 2
and nick_name like CONCAT('%',#{nickName},'%')
order by create_date desc limit #{pageNo},#{pageSize}

SELECT id,mobile_phone mobilePhone,face_src faceSrc,sex,nick_name nickName,create_by createBy,data_flag dataFlag, create_date createDate,TIMESTAMPDIFF(DAY, create_date,CURRENT_TIMESTAMP()) automaticThoughts,location FROM kjuns_user_info where (data_flag = 2 or data_flag=21)
and nick_name like CONCAT('%',#{nickName},'%')
order by create_date desc limit #{pageNo},#{pageSize}
