mapper.select:
SELECT count(*) FROM kjuns_report where 1=1 

	and report_type = #{contentType}


	and data_flag  = #{dataFlag}

SELECT id,report_id contentId, report_type contentType, user_id userId,create_by createBy,create_date createDate 
,data_flag dataFlag
FROM kjuns_report where 1=1 

	and report_type = #{contentType}


	and data_flag  = #{dataFlag}

order by create_date desc limit #{pageNo},#{pageSize}
